import{_ as t,c as i,o as a,ae as e}from"./chunks/framework.BHckWpNH.js";const b=JSON.parse('{"title":"驾校-考科目一时记下的笔记","description":"","frontmatter":{},"headers":[],"relativePath":"blog/2022/2022-07-16.驾校-科目一笔记.md","filePath":"blog/2022/2022-07-16.驾校-科目一笔记.md"}'),r={name:"blog/2022/2022-07-16.驾校-科目一笔记.md"};function d(o,l,h,n,_,s){return a(),i("div",null,l[0]||(l[0]=[e('<h1 id="驾校-考科目一时记下的笔记" tabindex="-1">驾校-考科目一时记下的笔记 <a class="header-anchor" href="#驾校-考科目一时记下的笔记" aria-label="Permalink to &quot;驾校-考科目一时记下的笔记&quot;">​</a></h1><h2 id="自定义名词解释" tabindex="-1">自定义名词解释 <a class="header-anchor" href="#自定义名词解释" aria-label="Permalink to &quot;自定义名词解释&quot;">​</a></h2><table tabindex="0"><thead><tr><th>名词</th><th>解释</th></tr></thead><tbody><tr><td>普通车,普车</td><td>校车,危险运输车,客运车以外的车</td></tr><tr><td>校客危,三车</td><td>校车,危险运输车,客运车</td></tr><tr><td>客危</td><td>客运车,危险运输车</td></tr><tr><td>普路</td><td>城市快速路,高速路以外的路</td></tr><tr><td>速路</td><td>城市快速路,高速路</td></tr><tr><td>顶扣</td><td>扣满十二分</td></tr><tr><td>违</td><td>违反,不按规定</td></tr></tbody></table><h2 id="口诀隐式规则" tabindex="-1">口诀隐式规则 <a class="header-anchor" href="#口诀隐式规则" aria-label="Permalink to &quot;口诀隐式规则&quot;">​</a></h2><ul><li>都会遵循<code>所有车&gt;普通车&gt;校车&gt;客车&gt;货车&gt;危险运输车&gt;三车</code>这样的顺序来编写(不过一般都会考虑将车型编进口诀,知道这么个东西就行)</li></ul><h2 id="扣分题" tabindex="-1">扣分题 <a class="header-anchor" href="#扣分题" aria-label="Permalink to &quot;扣分题&quot;">​</a></h2><h3 id="_1分" tabindex="-1">1分 <a class="header-anchor" href="#_1分" aria-label="Permalink to &quot;1分&quot;">​</a></h3><ol><li>不系安全带</li><li>摩托车不带头盔</li><li>不按规定会车；普通路上违规倒车掉头</li><li>不按规定使用灯光</li><li>违反禁令标志、禁止标线（如：压双黄线、单黄线,包含违规停车）</li><li>载货长度、宽度、高度超过规定的</li><li>【没安全检验的普通车，扣1分】驾驶未按规定定期进行安全技术检验的公路客运汽车、旅游客运汽车、危险物品运输车辆以外的机动车上道路行驶的，一次记1分。</li><li>超载未达30%</li><li>【校客危，普通道路超速10%-20%】驾驶校车、中型以上载客载货汽车、危险物品运输车辆在普通路上超速10%-20%</li></ol><p>口诀: 违会掉灯盔带禁,普车不检货过三,十载三车速一二</p><h3 id="_3分" tabindex="-1">3分 <a class="header-anchor" href="#_3分" aria-label="Permalink to &quot;3分&quot;">​</a></h3><ol><li>普通车载人超载20%-50%；</li><li>普通车在普通路上超速20%-50%；</li><li>货车超载30%-50%的，或者违反规定载客的；</li><li>（所有车）在高速公路或者城市快速路上不按规定车道行驶的；</li><li>不按规定超车、让行；普通路上逆行的；</li><li>借道超车或者占用对面车道、穿插等候车辆的；</li><li>打电话</li><li>行经人行横道不按规定减速、停车、避让行人的；</li><li>不按规定避让校车的；</li><li>驾驶不按规定安装机动车号牌的机动车上道路行驶的；</li><li>在道路上车辆发生故障、事故停车后，不按规定使用灯光或者设置警告标志的；</li><li>【没安全检验的“客危”，扣3分】驾驶未按规定定期进行安全技术检验的公路客运汽车、旅游客运汽车、危险物品运输车辆上道路行驶的；</li><li>【校车没安检或存在安全隐患】驾驶校车上道路行驶前，未对校车车况是否符合安全技术要求进行检查，或者驾驶存在安全隐患的校车上道路行驶的；</li><li>【货车疲劳驾驶】连续驾驶载货汽车超过4小时未停车休息或者停车休息时间少于20分钟的；</li><li>【在高速上龟速行使】驾驶机动车在高速公路上行驶低于规定最低时速的。</li></ol><p>口诀:违道超让校电逆,借占道穿不让人,速路行驶速低规(龟),牌乱故停违灯标,普车超载十二五,它走普路超速同,校患未检货未检,42不休让货(祸)行,超载三五货违客</p><h3 id="_6分" tabindex="-1">6分 <a class="header-anchor" href="#_6分" aria-label="Permalink to &quot;6分&quot;">​</a></h3><ol><li>驾驶校车、公路客运汽车、旅游客运汽车载人超载未达到20%；</li><li>驾驶7座以上载客汽车载人超载20%-50%；</li><li>其他普通车载人超载50%-100%的；</li><li>驾驶校车、中型以上载客载货汽车、危险物品运输车辆在高速公路、城市快速路上超速未达到20%，或者在普通路上超速20%-50%的；</li><li>普通车在高速公路、城市快速路上超速20%-50%；在普通路上超速50%以上的；</li><li>货车超载50%以上的；</li><li>【运危险品没挂警示标志】驾驶机动车载运爆炸物品、易燃易爆化学物品以及剧毒、放射性等危险物品，未按指定的时间、路线、速度行驶或者未悬挂警示标志并采取必要的安全措施的；</li><li>【闯红灯】驾驶机动车不按交通信号灯指示通行的；</li><li>机动车驾驶证被暂扣或者扣留期间驾驶机动车的；</li><li>造成致人轻微伤或者财产损失的交通事故后逃逸，尚不构成犯罪的；</li><li>违法占用应急车道</li></ol>',14)]))}const u=t(r,[["render",d]]);export{b as __pageData,u as default};
